mapper.select:
select

from erpsys_record

	
		where record_money between #{startMoney,jdbcType=INTEGER}
		and
		#{endMoney,jdbcType=INTEGER}
	


	
		where record_money > #{startMoney,jdbcType=INTEGER}
	


	
		where record_money < #{endMoney,jdbcType=INTEGER}
	

limit #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(*)
from erpsys_record

	
		where record_money between #{startMoney,jdbcType=INTEGER}
		and
		#{endMoney,jdbcType=INTEGER}
	


	
		where record_money > #{startMoney,jdbcType=INTEGER}
	


	
		where record_money < #{endMoney,jdbcType=INTEGER}
